export default {
    namespaced: true,
    state: {
        shoppingCart: JSON.parse(sessionStorage.getItem('shoppingCart')) || []
    },
    mutations: {
        getShoppingCartNumber(state, id) {
            state.shoppingCart.some(
                (item) => {
                    if (item.id === id) {
                        console.log(item.number);
                        // return item.number
                    }
                }
            )

            let a = [1,2,3,4]

            a.forEach()
            a.map((row) => {
                // do something
                return ''
            })
            a.filter((row) => {
                return true || false
            })
            a.find()

            a.every()
            a.some((row) => {
                return true || false
            })

            a.reduce((prevData, row) => {

                if (xxx) {
                    return prevData + 5
                }
            }, 0)

            栈结构
            双向链表
            单项链表


            a.unshift()
            a.shift()
            a.pop()
            a.push()

            a.slice()
            a.join()

            Array.isArray(a)

            a.includes()
            a.indexOf()

            a.at(-1)
            a[a.length - 1]

            a.length = 0
            a


        },
        JIA(state, id) {
            state.shoppingCart.forEach((item) => {
                if (item.id === id) { item.number++ }
            })
            // console.log(state);  
        },
        JIAN(state, id) {
            state.shoppingCart.forEach((item) => {
                if (item.id === id) {
                    item.number--
                }
            })

        },
        setShoppingCart(state, data) {
            // 设置购物车状态
            state.shoppingCart = data;
        },
        unshiftShoppingCart(state, data) {
            // 添加购物车
            // 用于在商品详情页点击添加购物车,后台添加成功后，更新vuex状态
            state.shoppingCart.unshift(data)
            sessionStorage.setItem('shoppingCart', JSON.stringify(state.shoppingCart))
        },
        deleteShoppingCart(state, id) {
            if (confirm('确定删除吗？')) {
                state.shoppingCart = state.shoppingCart.filter((item) => {
                    return item.id != id
                })
                sessionStorage.setItem('shoppingCart', JSON.stringify(state.shoppingCart))
            }
        },
        changeCheck(state, id) {
            state.shoppingCart.forEach((item) => {
                if (item.id === id) { item.check = !item.check }
            })
        }

    },
    actions: {
        jian(context, id) {
            context.state.shoppingCart.forEach((item) => {
                if (item.id === id && item.number > 1) {
                    context.commit('JIAN', id)
                }
            })
        },
        unshiftShoppingCart({ commit }, data) {
            commit('unshiftShoppingCart', data);
        },


    },
    getters: {
        // getShoppingCartNumber(state,id) {
        //     state.shoppingCart.forEach((item)=>{
        //         if(item.id===id){
        //             return item.number
        //         }
        //     })
        //   },
        getTotalPrice(state) {
            // 购物车勾选的商品总价格
            let totalPrice = 0;
            for (let i = 0; i < state.shoppingCart.length; i++) {
                const temp = state.shoppingCart[i];
                if (temp.check) {
                    totalPrice += temp.price * temp.number;
                }
            }
            console.log(totalPrice);
            return totalPrice;
        }
    },
}
