const state = {
    status:false,
    login:false
}
const mutations = {
    changeStatus:(state,data) => {
        state.status = data
        console.log(state.status);
    }
}
const actions = {
    check(context,data){
        // if(!context.state.status){
        //     context.commit('changeStatu', data)
        // }
        context.commit('changeStatus', data)
    },
}
const getters = {
    getLogin(state){
        return state.login
    }
}
export default {
    namespaced: true,//开启名命空间
    state,
    mutations,
    actions,
    getters
}
