export default {
    namespaced:true,
    state:{
        shoppingCart:JSON.parse(sessionStorage.getItem('shoppingCart')) || []
    },
    mutations:{
        JIA(state) {
            state.number = JSON.parse(sessionStorage.getItem('number')) || 1
            state.number++
            // console.log(state);  
        },
        JIAN(state) {
            state.number = JSON.parse(sessionStorage.getItem('number')) || 1
            state.number--
    
        },
        setShoppingCart (state, data) {
            // 设置购物车状态
            state.shoppingCart = data;
          },
        unshiftShoppingCart (state, data) {
        // 添加购物车
        // 用于在商品详情页点击添加购物车,后台添加成功后，更新vuex状态
            state.shoppingCart.unshift(data)
            sessionStorage.setItem('shoppingCart',JSON.stringify(state.shoppingCart))
            this.$message({
                showClose: true,
                message: '成功加入购物车',
                type: 'success'
              });
            },
        deleteShoppingCart (state, id) {
            if(confirm('确定删除吗？')){
                state.shoppingCart = state.shoppingCart.filter((item)=>{
                    return item.id != id
                })
                sessionStorage.setItem('shoppingCart',JSON.stringify(state.shoppingCart))
            }
            },
        changeCheck(state,id){
            state.shoppingCart.forEach((item)=>{
                if(item.id===id){item.check = !item.check}
            })
        }
      
    },
    actions:{
        jian(context) {
            if (context.state.number > 1) {
                context.commit('JIAN')
            }
        },
        unshiftShoppingCart ({ commit }, data) {
            commit('unshiftShoppingCart', data);
        },
          
      
    },
    getters:{
        getShoppingCartNumber(state,id) {
            state.shoppingCart.forEach((item)=>{
                if(item.id===id){
                    return item.number
                }
            })
          },
      
    }

}
