import axios from 'axios'
// import data from '../../../public/data.json'
export default{
    namespaced:true,//开启名命空间
    state:{
        msg:[],
        number:1
    },
    mutations:{
        JIA(state){
            state.nmuber++
            // console.log(state);  
        },
        JIAN(state){ 
            state.number--
        }
    },
    actions:{
            jian(context){
                if(context.state.number > 1){
                    context.commit('JIA')
                }
            },
                    // console.log(context);
                    //actions中拿不到state
            addMessage(context){    
                axios.get('/store_data.json').then(
                    response => {
                        // console.log(context.state.msg);
                        //console.log(response.data);
                        context.state.msg = response.data
                        // console.log(context.state.msg);
                    },
                    error => {  
                        alert(error.message)
                    } 
                    // function(msg){
                        //     console.log(msg.data);
                        //}
                    )
                }
        },  

    }
