const state = {
    status:false,
    login:JSON.parse(sessionStorage.getItem('login')) || false,
    address:[
        {
            id: 1,
            name: "陈同学",
            phone: "17723757598",
            address: "重庆 开州区 三溪镇xxxx"
          },
        {
            id: 2,
            name: "陈同学",
            phone: "19967748771",
            address: "广东 茂名市 化州市 杨梅镇 ***"
        }
    ]
}
const mutations = {
    changeStatus:(state,data) => {
        state.status = data
        // sessionStorage.setItem('login',data)
    }
}
const actions = {
    // check(context,data){
    //     // if(!context.state.status){
    //     //     context.commit('changeStatu', data)
    //     // }
    //     context.commit('changeStatus', data)
    // },

    /**
     * 检测用户是否登录
     * @param {Object} param0 
     * @returns {Promise}
     */
    check({commit, state}) {
        return new Promise((resolve, reject) => {
            if (state.status) {
                resolve(true)
            } else {
                reject(false)
            }
        })
    }
}
const getters = {
    getLogin(state){
        return state.login
    }
}
export default {
    namespaced: true,//开启名命空间
    state,
    mutations,
    actions,
    getters
}
