import axios from 'axios'
// import data from '../../../public/data.json'
export default{
    namespaced:true,//开启名命空间
    state:{
        msg:''
    },
    mutations:{
       
    },
    actions:{
        //actions中拿不到state
        addMessage(context){
            axios.get('/data.json').then(
                response => {
                    // console.log(context.state.msg);
                    //console.log(response.data);
                    context.state.msg.unshift(response.data)
                    console.log(response.data);
                },
                error => {  
                    alert(error.message)
                } 
                // function(msg){
                    //     console.log(msg.data);
                    //}
                    )
            }
        },

    }
