import axios from 'axios'
// import data from '../../../public/data.json'


const state = {
    productionData: [],
    status:false,
    number: JSON.parse(sessionStorage.getItem('number')) || 1
}

const mutations = {
    JIA(state) {
        state.number = JSON.parse(sessionStorage.getItem('number')) || 1
        state.number++
        // console.log(state);  
    },
    JIAN(state) {
        state.number = JSON.parse(sessionStorage.getItem('number')) || 1
        state.number--

    },
    SET_PRODUCTION_DATA: (state, data) => {
        state.productionData = data
    },
    changeStatus:(state,data) => {
        state.status = data
        console.log(state.status);
    }
}

const actions = {
    jian(context) {
        if (context.state.number > 1) {
            context.commit('JIAN')
        }
    },
    check(context,data){
        console.log(context.state.status);
        // if(!context.state.status){
        //     context.commit('changeStatu', data)
        // }
        context.commit('changeStatu', data)
    },
    // console.log(context);
    //actions中拿不到state
    addMessage(context) {
        axios.get('/store_data.json').then(
            response => {
                // console.log(context.state.msg);
                //console.log(response.data);
                context.commit('SET_PRODUCTION_DATA', response.data)
                context.state.status = true
                console.log('获取了axois数据');
                // console.log(context.state.msg);
            },
            error => {
                alert(error.message)
            }
            // function(msg){
            //     console.log(msg.data);
            //}
        )
    },
}
const getters = {
   number(state) {
       return state.number

   },
}


export default {
    namespaced: true,//开启名命空间
    state,
    mutations,
    actions,
    getters

    // getters: {
    //     number(state) {
    //         return state.number

    //     },
    // }

}
